import React, { useEffect } from 'react';
import styled from '@emotion/styled';
import { connect } from 'react-redux';
import { getReviewForSlug } from '../../actions/review';
import OrderReview from './OrderReview';

const ReviewsContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
`;

// review dynamically generated by users who have ordered products
const OrderReviews = ({
  review: { reviews, loading },
  getReviewForSlug,
  slug,
}) => {
  useEffect(() => {
    getReviewForSlug(slug);
  }, [getReviewForSlug]);

  if (loading) return <div> I am loading! </div>;

  return (
    <ReviewsContainer>
      {reviews &&
        reviews.length > 0 &&
        reviews.map(review => <OrderReview key={review._id} review={review} />)}
    </ReviewsContainer>
  );
};

const mapStateToProps = state => ({
  review: state.review,
});

export default connect(
  mapStateToProps,
  { getReviewForSlug },
)(OrderReviews);
